print("Part 1:", sum(map(lambda elves: 1 if (elves[0][0] <= elves[1][0] and elves[0][1] >= elves[1][1]) or (elves[0][0] >= elves[1][0] and elves[0][1] <= elves[1][1]) else 0, map(lambda elves: ((int(elves[0].split("-")[0]), int(elves[0].split("-")[1])), (int(elves[1].split("-")[0]), int(elves[1].split("-")[1]))), map(lambda line: line.strip().split(","), data := open("input.txt").read().strip().split())))))
